#!/bin/bash

python TreeQuickScan.py -d SimpleMuonsAllSignsNtuple -c "lep_id[0]*lep_id[1]<0 && lep_isGlobalMuon[0] && lep_isTrackerMuon[0] && fabs(lep_eta[0]) < 2.4 && abs(lep_dB[0]) < 0.2 && lep_sumPt[0] / lep_tk_pt[0] < 0.10 && lep_glb_numberOfValidTrackerLayers[0] > 5 && lep_glb_numberOfValidPixelHits[0] >= 1 && ( (lep_glb_numberOfValidMuonHits[0] > 0) || (lep_tuneP_numberOfValidMuonHits[0] > 0) ) && lep_numberOfMatchedStations[0]>1 && lep_pt_err[0] / lep_pt[0] < 0.3 && lep_isGlobalMuon[1] && lep_isTrackerMuon[1] && fabs(lep_eta[1]) < 2.4 && abs(lep_dB[1]) < 0.2 && lep_sumPt[1] / lep_tk_pt[1] < 0.10 && lep_glb_numberOfValidTrackerLayers[1] > 5 && lep_glb_numberOfValidPixelHits[1] >= 1 && ( (lep_glb_numberOfValidMuonHits[1] > 0) || (lep_tuneP_numberOfValidMuonHits[1] > 0) ) && lep_numberOfMatchedStations[1]>1 && lep_pt_err[1] / lep_pt[1] < 0.3 && vertex_chi2 < 20 && cos_angle>-0.9998 && lep_pt[0]>27 && lep_pt[1]>27 && vertex_m>60" -v run -v lumi -v event -v vertex_m -v lep_pt[0] -v lep_pt[1] > dimuons_M60_Pt27.txt
